import React, { useState } from "react";

function App() {
  const [query, setQuery] = useState("");
  const [chat, setChat] = useState([]);

  // const send = async () => {
  //   const res = await fetch("http://localhost:8000/query", {
  //     method: "POST",
  //     headers: {"Content-Type": "application/json"},
  //     body: JSON.stringify({ text: query }),
  //   });
  //   console.log(res, 'res')
  //   const data = await res.json();
  //   setChat([...chat, { user: query, bot: data.answer }]);
  //   setQuery("");
  // };
  const send = async () => {
  try {
    console.log("Sending:", query);

    const res = await fetch("http://localhost:8000/query", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text: query }),
    });

    const data = await res.json();
    console.log("Backend JSON:", data);

    // Safely get text if answer is object or string
    let botReply = "";

    if (typeof data.answer === "string") {
      botReply = data.answer;
    } else if (typeof data.answer === "object" && data.answer !== null) {
      // Try to extract text property
      if ("text" in data.answer) botReply = data.answer.text;
      else botReply = JSON.stringify(data.answer);
    } else {
      botReply = data.error ?? "No valid response";
    }

    setChat([...chat, { user: query, bot: botReply }]);

  } catch (err) {
    console.error("Fetch error:", err);
    setChat([...chat, { user: query, bot: "Network or server error" }]);
  }

  setQuery("");
};


  return (
    <div style={{ padding: 20 }}>
      <h2>AI LinkedIn Assistant (Demo)</h2>
      <div>
        {chat.map((c, i) => (
          <div key={i}>
            <b>You:</b> {c.user} <br />
            <b>Coach:</b> {c.bot}
            <hr />
          </div>
        ))}
      </div>

      <input
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={send}>Send</button>
    </div>
  );
}

export default App;
